import React from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends React.Component {
  state = {
    counter: 0,
  };

  AddCounter = () => {
    let counter = this.state.counter;
    counter = counter + 1;
    this.setState({
      counter,
    });
  };

  SubtractCounter = () => {
    let counter = this.state.counter;
    if (counter - 1 >= 0) {
      counter = counter - 1;
    } else {
      alert("Sorry! Counter can't go to the negative numbers!");
    }
    this.setState({
      counter,
    });
  };

  ResetCounter = () => {
    let counter = this.state.counter;
    counter = 0;
    this.setState({
      counter,
    });
  };

  render() {
    return (
      <div className="App">
        <h1>hello</h1>
      </div>
    );
  }
}

export default App;
