import React from "react";
import "./App.css";
import DivBlock from "./componets/DivBlock";

class App extends React.Component {
  state = {
    blocks: [
      [15, 2, 1, 12],
      [8, 5, 6, 11],
      [4, 9, 10, 7],
      [3, 14, 13, null],
    ],
    nullTracker: 15,
  };

  moveToNull = (index, index2) => {
    const blocks = this.state.blocks;
    let nullTracker = this.state.nullTracker;
    console.log();
    if (blocks[index][index2] === null) {
      alert("You can't move this block");
      return;
    } else if (blocks[index].indexOf(null) - 1 === index2) {
      console.log("WORKS");
      nullTracker -= 1;
    } else if (nullTracker + 1 === index) {
      blocks[nullTracker].key = blocks[index].key;
      blocks[index].key = null;
      nullTracker += 1;
    } else if (nullTracker - 4 === index) {
      blocks[nullTracker].key = blocks[index].key;
      blocks[index].key = null;
      nullTracker -= 4;
    } else if (nullTracker + 4 === index) {
      blocks[nullTracker].key = blocks[index].key;
      blocks[index].key = null;
      nullTracker += 4;
    } else {
      alert("You can't move this block");
      return;
    }
    this.setState({
      nullTracker,
      blocks,
    });
  };

  render() {
    return (
      <div>
        <DivBlock blocks={this.state.blocks} moveToNull={this.moveToNull} />
      </div>
    );
  }
}

export default App;
